	# run in a component script (propertyChange / button) where self is available
import system
from collections import defaultdict

data = value

if not data:
  # nothing to do
  return

# convert and get column names
pds = system.dataset.toPyDataSet(data)
cols = list(pds.getColumnNames())

def is_checked(v):

  if v is None:
      return False
  if isinstance(v, bool):
      return v
  if isinstance(v, (int, float)):
      return v != 0
  s = str(v).strip().lower()
  return s in ('1','true','t','y','yes','w')

def to_num(x):

  if x is None:
      return 0.0
  if isinstance(x, (int, float)):
      return float(x)
  s = str(x).strip()
  if s == '':
      return 0.0
  # remove common thousands separators and NBSPs
  s = s.replace(',', '').replace(' ', '').replace('\u00A0', '')
  try:
      return float(s)
  except:
      return 0.0

# Accumulate numeric values for source_1/2/3 per (date, farm_name) per hour-column
acc = defaultdict(lambda: defaultdict(list))
for row in pds:
  # row is a sequence -> build dict by zipping column names
  rowDict = dict(zip(cols, row))
  if is_checked(rowDict.get('selection')) and str(rowDict.get('Source_name')).lower() in ('source_1','source_2','source_3'):
      key = (rowDict.get('date'), rowDict.get('farm_name'))
      for col in cols:
          # skip meta columns
          if col.lower() in ('date','site_name','farm_name','status_name','selection','source_name'):
              continue
          acc[key][col].append(to_num(rowDict.get(col)))

# Build new dataset rows, updating avg_sources rows
newRows = []

for row in pds:  # include all rows
  rowDict = dict(zip(cols, row))

  if str(rowDict.get('Source_name')).lower() == 'avg_sources':
      key = (rowDict.get('date'), rowDict.get('farm_name'))
      if key in acc:
          for col, vals in acc[key].items():
              if col in cols:  # make sure it's a valid column
                  if vals:
                      # average (rounded int)
                      rowDict[col] = int(round(sum(vals) / float(len(vals))))
                      # for sum: rowDict[col] = int(round(sum(vals)))
                  else:
                      rowDict[col] = 0

  # always output exactly one value per column in order
  rowValues = [rowDict[c] if c in rowDict else None for c in cols]
  newRows.append(rowValues)

# Rebuild dataset using original schema
newDs = system.dataset.toDataSet(cols, newRows)
return newDs

